#include "suggest.h"

void
reread_dict(char *host)
{
	CLIENT *clnt;

#ifndef	DEBUG
	clnt = clnt_create (host, SUGGEST, SUGGESTVERS, "udp");
	if (clnt == NULL) {
		clnt_pcreateerror (host);
		exit (1);
	}
#endif	/* DEBUG */

	reload_dictionary_2(NULL, clnt);

#ifndef	DEBUG
	clnt_destroy (clnt);
#endif	 /* DEBUG */

}

void
suggest_1(char *host, char *arg, char *user, char *collection)
{
	CLIENT *clnt;
	enum clnt_stat retval_1;
	numbest_res *result_1;
	struct senddata args = {
		arg, user, (collection ? collection : "")
	};

#ifndef	DEBUG
	clnt = clnt_create (host, SUGGEST, SUGGESTVERS, "udp");
	if (clnt == NULL) {
		clnt_pcreateerror (host);
		exit (1);
	}
#endif	/* DEBUG */

	result_1 = get_best_results_2(&args, clnt);
	if (!retval_1)// != RPC_SUCCESS) {
	{

		clnt_perror (clnt, "call failed");
	}
	else {
		if (result_1->_errno == 0) {
			namelist nl;

			for (nl = result_1->numbest_res_u.list;
			     nl != NULL;
			     nl = nl->next) {
				printf("Name: %s/%d\n", nl->name, nl->frequency);
			}
		}
		else {
			printf("error...\n");
		}
		//printf("word/freq: %s/%d\n", result_1->name, result_1->frequency);
	}

#ifndef	DEBUG
	clnt_destroy (clnt);
#endif	 /* DEBUG */
}


int
main (int argc, char *argv[])
{
	char *host;

	if (argc < 5) {
		printf ("usage: %s server_host arg user collection\n", argv[0]);
		exit (1);
	}
	host = argv[1];
	if (strcmp(argv[2], "__rebuild") == 0) {
		reread_dict(host);
	} else {
		suggest_1 (host, argv[2], argv[3], argv[4]);
	}
	//_exit(0); /* avoid segfault */

	return 0;
}
